import React, { useState } from "react";
import { RiMenu3Line, RiCloseLine } from "react-icons/ri";
// import logo from "../../assets/logo.svg";
import "./navbar.css";

const Menu = () => (
  <>
    <p>
      <a href="#about">About</a>
    </p>
    <p>
      <a href="#projects">Projects</a>
    </p>
    <p>
      <a href="#involvement">Involvement</a>
    </p>
    <p>
      <a href="#contact">Contact</a>
    </p>
  </>
);

const Navbar = () => {
  const [toggleMenu, setToggleMenu] = useState(false);

  // Function to toggle the menu and overlay
  const toggleMenuHandler = () => {
    setToggleMenu((prevState) => !prevState);
  };

  return (
    <div className="navbar">
      {/* ... existing JSX ... */}
      <div className="navbar-menu">
        {toggleMenu ? (
          <div
            className="navbar-menu_overlay"
            onClick={toggleMenuHandler}></div>
        ) : null}
        {toggleMenu ? (
          <RiCloseLine color="#fff" size={27} onClick={toggleMenuHandler} />
        ) : (
          <RiMenu3Line color="#fff" size={27} onClick={toggleMenuHandler} />
        )}
        {toggleMenu && (
          <div className="navbar-menu_container active scale-up-center">
            <div className="navbar-menu_container-links">
              <Menu />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Navbar;
